import { Injectable } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { DialogComponent } from './dialog.component';

@Injectable({
  providedIn: 'root',
})
export class DialogService {
  readonly #confirm$ = new Subject<void>();
  readonly #types = {
    success: {
      'icon__check-circle--md': true,
      'dialog__form__icon--success': true,
    },
    danger: {
      'icon__alert-circle--md': true,
      'dialog__form__icon--danger': true,
    },
    warning: {
      'icon__help-circle--md': true,
      'dialog__form__icon--warning': true,
    },
  };

  #dialog!: DialogComponent;
  public setDialog(dialog: DialogComponent): void {
    this.#dialog = dialog;
  }

  public confirm(): void {
    this.#confirm$.next();
  }

  public showPositiveFeedback(data: {
    message: string;
    textButton: string;
  }): Observable<void> {
    return this.showFeedback(
      Object.assign(data, { type: this.#types.success }),
    );
  }

  public showNegativeFeedback(data: {
    message: string;
    textButton: string;
  }): Observable<void> {
    return this.showFeedback(Object.assign(data, { type: this.#types.danger }));
  }

  private showFeedback(data: {
    message: string;
    textButton: string;
    type: unknown;
  }): Observable<void> {
    const dialog = this.#dialog;
    dialog.message = data.message;
    dialog.textButtonOk = data.textButton;
    dialog.type = data.type;
    dialog.showButtonCancel = false;
    dialog.show();
    return this.#confirm$.asObservable();
  }

  public showQuestion(data: {
    message: string;
    textButtonOk: string;
    textButtonCancel: string;
  }): Observable<void> {
    const dialog = this.#dialog;
    dialog.message = data.message;
    dialog.textButtonOk = data.textButtonOk;
    dialog.textButtonCancel = data.textButtonCancel;
    dialog.type = this.#types.warning;
    dialog.showButtonCancel = true;
    dialog.show();
    return this.#confirm$.asObservable();
  }
}

import { EventEmitter, Injectable } from '@angular/core';
import { AbstractControl, FormArray, FormGroup } from '@angular/forms';
import { Observable, Subject } from 'rxjs';

@Injectable({
  providedIn: 'any',
})
export class FormService<DTO = unknown> {
  readonly #submit = new Subject<DTO>();
  readonly #remove = new Subject<void>();

  public submitDto(form: FormGroup | FormArray, createDtoFn: () => DTO): void {
    if (form.valid) {
      const dto = createDtoFn();
      this.#submit.next(dto);
    } else {
      this.checkFormValidations(form);
    }
  }

  private checkFormValidations(form: FormGroup | FormArray): void {
    Object.keys(form.controls).forEach((field) => {
      const control = form.get(field) as AbstractControl;
      control.markAsDirty();
      control.markAsTouched();
      const status = control.status;
      (control.statusChanges as EventEmitter<string>).emit('CHECK_COMPONENT');
      (control.statusChanges as EventEmitter<string>).emit(status);
      if (control instanceof FormGroup || control instanceof FormArray) {
        this.checkFormValidations(control);
      }
    });
  }

  public onSubmitDto(): Observable<DTO> {
    return this.#submit.asObservable();
  }

  public remove(): void {
    this.#remove.next(undefined);
  }

  public onRemove(): Observable<void> {
    return this.#remove.asObservable();
  }
}

import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, take } from 'rxjs';

@Injectable({
  providedIn: 'any',
})
export class HttpService {
  constructor(private readonly _http: HttpClient) {}

  private get options(): {
    headers?: HttpHeaders;
    params?: HttpParams;
  } {
    return {
      params: this.params,
    };
  }

  private get params(): HttpParams {
    return new HttpParams();
  }

  public getById<T>(apiPath: string, id: string | number): Observable<T> {
    return this._http.get<T>(`${apiPath}/${id}`).pipe(take(1));
  }

  public create<T, DTO = unknown>(
    apiPath: string,
    resource: DTO,
  ): Observable<T> {
    return this._http.post<T>(apiPath, resource, this.options).pipe(take(1));
  }

  public update<T, DTO = unknown>(
    apiPath: string,
    id: number,
    resource: DTO,
  ): Observable<T> {
    return this._http
      .patch<T>(`${apiPath}/${id}`, resource, this.options)
      .pipe(take(1));
  }

  public delete<T = unknown>(apiPath: string, id: number): Observable<T> {
    return this._http.delete<T>(`${apiPath}/${id}`, this.options).pipe(take(1));
  }

  public search<T>(apiPath: string): Observable<T> {
    return this._http.get<T>(`${apiPath}`).pipe(take(1));
  }
}

