export class CpfValidator {
  public isValid(value: string): boolean {
    if (!value || typeof value !== 'string') return false;
    if (value.length !== 11) return false;
    if (this.isSequencie(value)) return false;

    const { totalValue, resultValue } = this.startValues(value);
    let calcValue = resultValue;
    calcValue += this.calcDigit(value, 8);
    calcValue += this.calcDigit(value, 9);

    return totalValue === calcValue;
  }

  private isSequencie(value: string): boolean {
    const sequencie = value[0].repeat(value.length);
    return sequencie === value;
  }

  private startValues(value: string): {
    totalValue: number;
    resultValue: number;
  } {
    let totalValue = 0;
    let resultValue = 0;
    for (let i = 0; i < value.length; i++) {
      const digit = Number(value[i]);
      totalValue += digit;
      if (i < 9) {
        resultValue += digit;
      }
    }

    return { totalValue, resultValue };
  }

  private calcDigit(value: string, indexEnd: number): number {
    let total = 0;
    for (let i = indexEnd, j = 2; i > -1; i--, j++) {
      const digit = Number(value[i]);
      total += digit * j;
    }
    const digit = 11 - (total % 11);
    return digit > 9 ? 0 : digit;
  }
}

import { ICustomer } from './interfaces/customer.interface';

export class CustomerDataForm {
  readonly birthDate = { value: '', disabled: false };
  readonly cpf = { value: '', disabled: false };
  readonly createdAt = { value: null, disabled: true };
  readonly email = { value: '', disabled: false };
  readonly monthlyIncome = { value: null, disabled: false };
  readonly name = { value: '', disabled: false };

  static fromCustomer(customer: ICustomer): CustomerDataForm {
    const dataForm = new CustomerDataForm();
    for (const [key, attr] of Object.entries(dataForm)) {
      if (Object.prototype.hasOwnProperty.call(customer, key)) {
        attr.value = (customer as unknown as { [key: string]: string })[key];
      }
    }
    return dataForm;
  }

  public disableCpf(): this {
    this.cpf.disabled = true;
    return this;
  }
}

import { convertToDate } from 'src/app/helper/date.helper';

export class CustomerDto {
  birthDate: string;
  cpf?: string;
  email: string;
  monthlyIncome: number;
  name: string;

  constructor(data: { [key: string]: string }) {
    this.name = data['name'].trim();
    this.email = data['email'].trim();

    const cpf = data['cpf'];
    if (cpf) {
      this.cpf = cpf.trim().replace(/\D/g, '');
    } else {
      delete this.cpf;
    }

    this.monthlyIncome = +data['monthlyIncome']
      .replace(/\./g, '')
      .replace(',', '.');

    this.birthDate = convertToDate(data['birthDate']).toISOString();
  }
}

import { convertToDate } from '../helper/date.helper';

export class DateValidator {
  public isValid(value: string): boolean {
    const date = convertToDate(value);
    return !(!(date instanceof Date) || isNaN(date.getTime()));
  }

  public minAge(value: string, min: number): boolean {
    const age = this.getAge(value);
    return age >= min;
  }

  public getAge(value: string): number {
    const date = convertToDate(value);
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    today.setFullYear(today.getFullYear() - age);
    if (date > today) {
      age--;
    }
    return age;
  }

  public maxAge(value: string, max: number): boolean {
    const age = this.getAge(value);
    return age <= max;
  }
}

import { UntypedFormControl } from '@angular/forms';
import { CpfValidator } from './validators/cpf.validator';
import { DateValidator } from './validators/date.validator';
import { NameValidator } from './validators/name.validator';

export class FormValidations {
  static #cpfValidator = new CpfValidator();
  static #dateValidator = new DateValidator();
  static #nameValidator = new NameValidator();

  static getErrorMsg(
    _: string,
    validatorName: string,
    validatorValue: { [key: string]: string },
  ): string {
    const config: { [key: string]: string } = {
      required: `campo obrigatÃ³rio`,
      minlength: `precisa ter no mÃ­nimo ${validatorValue['requiredLength']} caractere(s)`,
      maxlength: `precisa ter no mÃ¡ximo ${validatorValue['requiredLength']} caractere(s)`,
      min: `o valor mÃ­nimo permitido Ã© ${validatorValue['min']}`,
      cpfInvalid: `CPF invÃ¡lido`,
      dateInvalid: `data invÃ¡lida`,
      lastNameInvalid: `percisa ter nome e sobrenome`,
      minAgeInvalid: `idade mÃ­nima permitida Ã© ${validatorValue} ${
        Number(validatorValue) > 1 ? 'anos' : 'ano'
      }`,
      maxAgeInvalid: `idade mÃ¡xima permitida Ã© ${validatorValue} ${
        Number(validatorValue) > 1 ? 'anos' : 'ano'
      }`,
    };

    return config[validatorName];
  }

  static cpf(control: UntypedFormControl): object | null {
    const value = control.value?.replace(/[^\d]+/g, '');
    return value && !FormValidations.#cpfValidator.isValid(value)
      ? { cpfInvalid: true }
      : null;
  }

  static date(control: UntypedFormControl): object | null {
    const value = control.value?.trim();
    return value && !FormValidations.#dateValidator.isValid(value)
      ? { dateInvalid: true }
      : null;
  }

  static minAge(min: number): (control: UntypedFormControl) => object | null {
    return (control: UntypedFormControl) => {
      const value = control.value?.trim();
      return value && !FormValidations.#dateValidator.minAge(value, min)
        ? { minAgeInvalid: min }
        : null;
    };
  }

  static maxAge(max: number): (control: UntypedFormControl) => object | null {
    return (control: UntypedFormControl) => {
      const value = control.value?.trim();
      return value && !FormValidations.#dateValidator.maxAge(value, max)
        ? { maxAgeInvalid: max }
        : null;
    };
  }

  static lastName(control: UntypedFormControl): object | null {
    const value = control.value?.trim();
    return value && !FormValidations.#nameValidator.hasLastName(value)
      ? { lastNameInvalid: true }
      : null;
  }
}

export class NameValidator {
  public hasLastName(value: string): boolean {
    return value.trim().split(' ').length !== 1;
  }
}

