import {
  AfterContentInit,
  Directive,
  ElementRef,
  HostListener,
  OnInit,
} from '@angular/core';
import { AbstractControl, NgControl } from '@angular/forms';
import { isKeyNotNumber, isKeyNumber } from '../helper/number.helper';

@Directive({
  selector: '[appCpfMask]',
})
export class CpfMaskDirective implements OnInit, AfterContentInit {
  constructor(
    private readonly _elementRef: ElementRef,
    private readonly _ngControl: NgControl,
  ) {}

  get control() {
    return this._ngControl.control as AbstractControl;
  }

  get value() {
    return this.control.value;
  }

  set value(value: string) {
    this.control.setValue(value);
  }

  get isMasked() {
    return /^\d{3}\.\d{3}\.\d{3}-\d{2}$/.test(this.value);
  }

  public ngOnInit(): void {
    this._elementRef.nativeElement.onkeypress = (event: KeyboardEvent) => {
      if (isKeyNotNumber(event.key) || this.isMasked) {
        event.preventDefault();
      }
    };
  }

  public ngAfterContentInit(): void {
    this.addMask();
  }

  @HostListener('keyup', ['$event'])
  public onKeyUp(event: KeyboardEvent) {
    const isNotMasked = !this.isMasked;
    if (isNotMasked) {
      if (isKeyNumber(event.key)) {
        this.addMask();
      } else if (event.key === '.') {
        this.addDotIfPossible();
      } else if (event.key === '-') {
        this.addDashIfPossible();
      }
    }
  }

  private addMask(): void {
    setTimeout(() => {
      let value = this.value;
      if (value) {
        value = value.replace(/\D/g, '');
        value = value.replace(/(\d{3})(\d)/, '$1.$2');
        value = value.replace(/(\d{3})(\d)/, '$1.$2');
        this.value = value.replace(/(\d{3})(\d{1,2})$/, '$1-$2');
      }
    });
  }

  private addDotIfPossible(): void {
    setTimeout(() => {
      const value = this.value;
      if (value) {
        const values = value.split('.');
        const lastIndex = values.length - 1;
        if (values.length < 3 && /^\d{3}$/.test(values[lastIndex])) {
          values[lastIndex] = `${values[lastIndex]}.`;
          this.value = values.join('');
        }
      }
    });
  }

  private addDashIfPossible(): void {
    setTimeout(() => {
      const value = this.value;
      if (
        value &&
        !value.includes('-') &&
        /^\d{3}\.\d{3}\.\d{3}$/.test(value)
      ) {
        this.value = `${value}-`;
      }
    });
  }

  @HostListener('paste', ['$event'])
  public onPaste() {
    setTimeout(() => this.addMask(), 150);
  }
}

import {
  AfterContentInit,
  Directive,
  ElementRef,
  HostListener,
  OnInit,
} from '@angular/core';
import { AbstractControl, NgControl } from '@angular/forms';
import {
  formatNumber,
  isKeyNotNumber,
  isKeyNumber,
} from '../helper/number.helper';

@Directive({
  selector: '[appCurrencyMask]',
})
export class CurrencyMaskDirective implements OnInit, AfterContentInit {
  constructor(
    private readonly _elementRef: ElementRef,
    private readonly _ngControl: NgControl,
  ) {}

  get control() {
    return this._ngControl.control as AbstractControl;
  }

  get value() {
    return this.control.value;
  }

  set value(value: string) {
    this.control.setValue(value);
  }

  public ngOnInit(): void {
    this._elementRef.nativeElement.onkeypress = (event: KeyboardEvent) => {
      if (isKeyNotNumber(event.key)) {
        event.preventDefault();
      }
    };
  }

  public ngAfterContentInit(): void {
    const value = this.value;
    if (value) {
      this.value = formatNumber(value);
    }
  }

  @HostListener('keyup', ['$event'])
  public onKeyUp(event: KeyboardEvent) {
    if (isKeyNumber(event.key)) {
      this.addMask();
    } else if (event.key === ',') {
      this.addCommaIfPossible();
    }
  }

  private addMask(): void {
    setTimeout(() => {
      let value = this.value;
      if (value) {
        value = value.replace(/\D/g, '').toString();
        value = (+value / 100).toFixed(2);
        value = value.replace('.', ',');
        this.value = value.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1.');
      }
    });
  }

  private addCommaIfPossible(): void {
    setTimeout(() => {
      const value = this.value;
      if (!value?.includes(',')) {
        this.value = `${value},`;
      }
    });
  }

  @HostListener('paste', ['$event'])
  public onPaste() {
    setTimeout(() => this.ngAfterContentInit(), 150);
  }
}

import { DatePipe } from '@angular/common';
import {
  AfterContentInit,
  Directive,
  ElementRef,
  HostListener,
  OnInit,
} from '@angular/core';
import { AbstractControl, NgControl } from '@angular/forms';
import { isKeyNotNumber, isKeyNumber } from '../helper/number.helper';

@Directive({
  selector: '[appDateMask]',
  providers: [DatePipe],
})
export class DateMaskDirective implements OnInit, AfterContentInit {
  constructor(
    private readonly _elementRef: ElementRef,
    private readonly _ngControl: NgControl,
    private readonly _datePipe: DatePipe,
  ) {}

  get control() {
    return this._ngControl.control as AbstractControl;
  }

  get value() {
    return this.control.value;
  }

  set value(value: string) {
    this.control.setValue(value);
  }

  get isMasked() {
    return /^[0-9]{2}[/]{1}[0-9]{2}[/]{1}[0-9]{4}$/g.test(this.value);
  }

  public ngOnInit(): void {
    this._elementRef.nativeElement.onkeypress = (event: KeyboardEvent) => {
      if (isKeyNotNumber(event.key) || this.isMasked) {
        event.preventDefault();
      }
    };
  }

  public ngAfterContentInit(): void {
    const value = this.value;
    if (value) {
      this.value = this._datePipe.transform(value, 'dd/MM/yyyy') as string;
    }
  }

  @HostListener('keyup', ['$event'])
  public onKeyUp(event: KeyboardEvent) {
    const isNotMasked = !this.isMasked;
    if (isNotMasked) {
      if (isKeyNumber(event.key)) {
        this.addMask();
      } else if (event.key === '/') {
        this.addBackslashIfPossible();
      }
    }
  }

  private addMask(): void {
    setTimeout(() => {
      let value = this.value;
      if (value?.split('/').length < 3) {
        value = value.replace(/\D/g, '');
        value = value.replace(/(\d{2})(\d)/, '$1/$2');
        this.value = value.replace(/(\d{2}\/\d{2})(\d)/, '$1/$2');
      }
    });
  }

  private addBackslashIfPossible(): void {
    setTimeout(() => {
      const value = this.value;
      if (value) {
        const values = value.split('/');
        if (values.length < 3) {
          const lastIndex = values.length - 1;
          values[lastIndex] = `${values[lastIndex]}/`.padStart(3, '0');
          this.value = values.join('');
        }
      }
    });
  }

  @HostListener('paste', ['$event'])
  public onPaste() {
    setTimeout(() => {
      const value = this.value;
      if (value) {
        const values = value.split('/');
        if (values.length === 1) {
          this.addMask();
        } else {
          this.value = values.map((v) => v.padStart(2, '0')).join('/');
        }
      }
    }, 150);
  }
}

