import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs';
import { DialogService } from 'src/app/components/dialog/dialog.service';
import { CustomerService } from '../customer.service';
import { CustomerDataForm } from '../models/customer.data-form';

@Component({
  selector: 'app-customer-edit',
  templateUrl: './customer-edit.component.html',
  styleUrls: ['./customer-edit.component.css'],
})
export class CustomerEditComponent implements OnInit, OnDestroy {
  readonly #subscripton = new Subscription();

  showForm = true;

  constructor(
    private readonly _customerService: CustomerService,
    private readonly _dialogService: DialogService,
    private readonly _route: ActivatedRoute,
  ) {}

  public ngOnInit(): void {
    this._customerService.initForm();
    const customerId = +(this._route.snapshot.paramMap.get('id') as string);
    this._customerService.getById(customerId).subscribe({
      next: (data) => {
        this._customerService.updateDataForm(
          CustomerDataForm.fromCustomer(data).disableCpf(),
        );
        this.setUpdate(customerId);
        this.setRemove(customerId);
        this.reloadForm();
      },
      error: (error) => {
        if (error.status === 404) {
          const sub = this._dialogService
            .showNegativeFeedback({
              message: 'Cliente nÃ£o encontrado!',
              textButton: 'OK',
            })
            .subscribe(() => {
              this._customerService.navigateToBack();
            });
          this.#subscripton.add(sub);
        }
      },
    });
  }

  private setUpdate(id: number): void {
    const sub = this._customerService.onUpdate(id).subscribe();
    this.#subscripton.add(sub);
  }

  private setRemove(id: number): void {
    const sub = this._customerService.onRemove(id).subscribe();
    this.#subscripton.add(sub);
  }

  private reloadForm(): void {
    this.showForm = false;
    setTimeout(() => {
      this.showForm = true;
    });
  }

  public ngOnDestroy(): void {
    this.#subscripton.unsubscribe();
  }
}

import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { IForm } from 'src/app/interfaces/form.interface';
import { FormValidations } from '../../../../form.validation';
import { CustomerService } from '../../customer.service';
import { CustomerDto } from '../../models/customer.dto';

@Component({
  selector: 'app-customer-form',
  templateUrl: './customer-form.component.html',
  styleUrls: ['./customer-form.component.css'],
})
export class CustomerFormComponent implements OnInit, IForm {
  formGroup!: FormGroup;

  @Input() removeDisabled = true;

  constructor(
    private readonly _customerService: CustomerService,
    private readonly _formBuilder: FormBuilder,
  ) {}

  public ngOnInit(): void {
    this.buildFormGroup();
  }

  private buildFormGroup(): void {
    const dataForm = this._customerService.dataForm;
    this.formGroup = this._formBuilder.group({
      birthDate: [
        dataForm.birthDate,
        [
          Validators.required,
          FormValidations.date,
          FormValidations.minAge(18),
          FormValidations.maxAge(60),
        ],
      ],
      cpf: [dataForm.cpf, [Validators.required, FormValidations.cpf]],
      createdAt: [dataForm.createdAt],
      email: [dataForm.email, [Validators.email]],
      monthlyIncome: [dataForm.monthlyIncome, [Validators.required]],
      name: [dataForm.name, [Validators.required, FormValidations.lastName]],
    });
  }

  public onSubmit(): void {
    this._customerService.submitDto(
      this.formGroup,
      () => new CustomerDto(this.formGroup.value),
    );
  }

  public returnToCustomers(): void {
    this._customerService.navigateToBack();
  }

  public onRemove(): void {
    this._customerService.remove();
  }
}

